"use client"

import { useEffect, useState } from 'react'
import { Tooltip } from '@shadcn/ui'

export default function ReadingProgressBar() {
  const [readingProgress, setReadingProgress] = useState(0)

  useEffect(() => {
    const updateReadingProgress = () => {
      const currentProgress = window.scrollY
      const scrollHeight = document.documentElement.scrollHeight - window.innerHeight
      if (scrollHeight) {
        setReadingProgress(Math.min((currentProgress / scrollHeight) * 100, 100))
      }
    }

    window.addEventListener('scroll', updateReadingProgress)

    return () => {
      window.removeEventListener('scroll', updateReadingProgress)
    }
  }, [])

  return (
    <div className="fixed top-0 left-0 z-50 w-full bg-gray-200 h-4 shadow-md">
      <div className="relative w-full h-full">
        <div 
          className="h-full bg-gradient-to-r from-blue-500 to-purple-600 rounded-full transition-all duration-300 ease-out"
          style={{ width: `${readingProgress}%` }}
        />
        <Tooltip content={`${Math.floor(readingProgress)}%`} delayDuration={200}>
          <div 
            className="absolute top-1/2 w-5 h-5 bg-white border-2 border-blue-500 rounded-full transform -translate-y-1/2 shadow-lg transition-transform duration-300"
            style={{ left: `${readingProgress}%` }}
          />
        </Tooltip>
        <div className="absolute right-4 top-1/2 transform -translate-y-1/2 text-sm text-gray-700">
          {Math.floor(readingProgress)}%
        </div>
      </div>
    </div>
  )
}

import { useState } from 'react'
import { Checkbox } from "@/components/ui/checkbox"
import { Label } from "@/components/ui/label"
import { Card, CardHeader, CardTitle, CardContent, CardFooter } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import Image from 'next/image'

interface Ingredient {
  id: string
  name: string
  amount: string
}

const ingredients: Ingredient[] = [
  { id: '1', name: 'Quinoa', amount: '1 cup' },
  { id: '2', name: 'Cherry tomatoes', amount: '1 cup, halved' },
  { id: '3', name: 'Cucumber', amount: '1/2, diced' },
  { id: '4', name: 'Red onion', amount: '1/4, finely chopped' },
  { id: '5', name: 'Feta cheese', amount: '1/2 cup, crumbled' },
  { id: '6', name: 'Kalamata olives', amount: '1/4 cup, pitted and sliced' },
  { id: '7', name: 'Extra virgin olive oil', amount: '2 tablespoons' },
  { id: '8', name: 'Lemon juice', amount: '2 tablespoons' },
  { id: '9', name: 'Fresh mint leaves', amount: '2 tablespoons, chopped' },
  { id: '10', name: 'Salt', amount: 'to taste' },
  { id: '11', name: 'Black pepper', amount: 'to taste' },
]

export default function IngredientChecklist() {
  const [checkedItems, setCheckedItems] = useState<{ [key: string]: boolean }>({})

  const toggleItem = (id: string) => {
    setCheckedItems(prev => ({ ...prev, [id]: !prev[id] }))
  }

  const handleSelectAll = () => {
    const allChecked = ingredients.every(item => checkedItems[item.id])
    const newCheckedItems: { [key: string]: boolean } = {}
    ingredients.forEach(item => {
      newCheckedItems[item.id] = !allChecked
    })
    setCheckedItems(newCheckedItems)
  }

  const handleReset = () => {
    setCheckedItems({})
  }

  const allChecked = ingredients.length > 0 && ingredients.every(item => checkedItems[item.id])

  const completedCount = Object.values(checkedItems).filter(Boolean).length

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 p-4">
      <Card className="w-full max-w-md mx-auto shadow-lg transition-transform transform hover:scale-105">
        <CardHeader className="flex flex-col items-center">
          <Image src="/healthy_eats.png" alt="HealthyEats" width={100} height={100} className="mb-4" />
          <CardTitle className="text-2xl font-bold text-center">HealthyEats Mediterranean Quinoa Salad</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-2">
              <Checkbox
                id="select-all"
                checked={allChecked}
                onCheckedChange={handleSelectAll}
              />
              <Label htmlFor="select-all" className="font-medium">Select All</Label>
            </div>
            <Button variant="secondary" size="sm" onClick={handleReset}>Reset</Button>
          </div>
          <h2 className="text-xl font-semibold mb-4">Ingredients Checklist ({completedCount}/{ingredients.length})</h2>
          <ul className="space-y-2">
            {ingredients.map((ingredient) => (
              <li key={ingredient.id} className="flex items-center space-x-2 p-2 rounded hover:bg-gray-50 transition-colors">
                <Checkbox
                  id={ingredient.id}
                  checked={checkedItems[ingredient.id] || false}
                  onCheckedChange={() => toggleItem(ingredient.id)}
                  className="transition-transform duration-300"
                />
                <Label
                  htmlFor={ingredient.id}
                  className={`flex-grow transition-colors duration-300 ${checkedItems[ingredient.id] ? 'line-through text-gray-500' : 'text-gray-700'}`}
                >
                  {ingredient.name} - {ingredient.amount}
                </Label>
              </li>
            ))}
          </ul>
        </CardContent>
        <CardFooter className="text-center text-sm text-gray-500">
          Gather your ingredients and enjoy cooking!
        </CardFooter>
      </Card>
    </div>
  )
}

'use client'

import { useState } from 'react'
import { Star } from 'lucide-react'
import { Button } from "@/components/ui/button"
import { Textarea } from "@/components/ui/textarea"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Card, CardHeader, CardContent, CardFooter } from "@/components/ui/card"
import { motion, AnimatePresence } from 'framer-motion'

interface Review {
  id: number
  author: string
  rating: number
  comment: string
  date: string
}

export default function ProductReviews() {
  const [reviews, setReviews] = useState<Review[]>([
    {
      id: 1,
      author: 'Jane Doe',
      rating: 5,
      comment: 'Absolutely love this product! It has made such a difference in my skincare routine.',
      date: '2023-05-15'
    },
    {
      id: 2,
      author: 'John Smith',
      rating: 4,
      comment: 'Great product, but I wish the packaging was more eco-friendly.',
      date: '2023-05-10'
    }
  ])
  const [newReview, setNewReview] = useState({ rating: 0, comment: '' })
  const [hoverRating, setHoverRating] = useState(0)

  const handleStarClick = (rating: number) => {
    setNewReview(prev => ({ ...prev, rating }))
  }

  const handleStarHover = (rating: number) => {
    setHoverRating(rating)
  }

  const handleStarLeave = () => {
    setHoverRating(0)
  }

  const handleCommentChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setNewReview(prev => ({ ...prev, comment: event.target.value }))
  }

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault()
    if (newReview.rating === 0 || newReview.comment.trim() === '') {
      alert('Please provide both a rating and a comment.')
      return
    }
    const review: Review = {
      id: reviews.length + 1,
      author: 'Anonymous',
      rating: newReview.rating,
      comment: newReview.comment,
      date: new Date().toISOString().split('T')[0]
    }
    setReviews(prev => [review, ...prev])
    setNewReview({ rating: 0, comment: '' })
  }

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-3xl font-bold mb-6 text-center text-primary">Customer Reviews</h2>

      {/* Review submission form */}
      <Card className="mb-8 p-6 bg-background shadow-lg hover:shadow-xl transition-shadow rounded-lg">
        <CardHeader>
          <h3 className="text-xl font-semibold mb-4">Write a Review</h3>
        </CardHeader>
        <CardContent>
          <div className="flex mb-4">
            {[1, 2, 3, 4, 5].map((star) => (
              <motion.div
                key={star}
                whileHover={{ scale: 1.2 }}
                whileTap={{ scale: 0.9 }}
              >
                <Star
                  className={`w-8 h-8 cursor-pointer transition-colors duration-200 ${
                    (hoverRating || newReview.rating) >= star ? 'text-yellow-400' : 'text-gray-300'
                  }`}
                  onClick={() => handleStarClick(star)}
                  onMouseEnter={() => handleStarHover(star)}
                  onMouseLeave={handleStarLeave}
                />
              </motion.div>
            ))}
          </div>
          <form onSubmit={handleSubmit}>
            <Textarea
              placeholder="Write your review here..."
              value={newReview.comment}
              onChange={handleCommentChange}
              className="mb-4 resize-none"
              rows={4}
            />
            <Button type="submit" className="w-full hover:bg-primary-dark transition-colors">
              Submit Review
            </Button>
          </form>
        </CardContent>
      </Card>

      {/* Existing reviews */}
      <AnimatePresence>
        <div className="space-y-6">
          {reviews.map((review) => (
            <motion.div
              key={review.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3 }}
            >
              <Card className="p-6 bg-background shadow-md hover:shadow-lg transition-shadow rounded-lg">
                <CardHeader className="flex items-center mb-4">
                  <Avatar className="w-12 h-12 mr-4">
                    <AvatarImage src={`https://api.dicebear.com/6.x/initials/svg?seed=${review.author}`} alt={review.author} />
                    <AvatarFallback>{review.author.slice(0, 2)}</AvatarFallback>
                  </Avatar>
                  <div>
                    <p className="text-lg font-semibold">{review.author}</p>
                    <p className="text-sm text-gray-500">{new Date(review.date).toLocaleDateString()}</p>
                  </div>
                  <div className="flex ml-auto">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <Star
                        key={star}
                        className={`w-5 h-5 ${
                          star <= review.rating ? 'text-yellow-400' : 'text-gray-300'
                        }`}
                      />
                    ))}
                  </div>
                </CardHeader>
                <CardContent>
                  <p className="text-gray-700">{review.comment}</p>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </div>
      </AnimatePresence>
    </div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Progress } from "@/components/ui/progress"
import { CheckIcon, FireIcon, HeartIcon, UserIcon } from "lucide-react"
import Image from "next/image"

export default function Component() {
  const [votes, setVotes] = useState({
    "Strength Training": 0,
    "Cardio": 0,
    "Yoga": 0,
    "HIIT": 0
  })
  const [userVoted, setUserVoted] = useState(false)
  const [totalVotes, setTotalVotes] = useState(0)

  const workoutOptions = [
    { name: "Strength Training", icon: <FireIcon className="w-6 h-6 text-red-500" /> },
    { name: "Cardio", icon: <UserIcon className="w-6 h-6 text-blue-500" /> },
    { name: "Yoga", icon: <HeartIcon className="w-6 h-6 text-green-500" /> },
    { name: "HIIT", icon: <CheckIcon className="w-6 h-6 text-yellow-500" /> },
  ]

  const handleVote = (option: string) => {
    if (!userVoted) {
      setVotes(prev => ({ ...prev, [option]: prev[option] + 1 }))
      setUserVoted(true)
      setTotalVotes(prev => prev + 1)
    }
  }

  useEffect(() => {
    // Simulate live updates
    const interval = setInterval(() => {
      const randomOption = Object.keys(votes)[Math.floor(Math.random() * 4)]
      setVotes(prev => ({ ...prev, [randomOption]: prev[randomOption] + 1 }))
      setTotalVotes(prev => prev + 1)
    }, 3000)

    return () => clearInterval(interval)
  }, [votes])

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-500 to-indigo-600 flex items-center justify-center p-4">
      <Card className="w-full max-w-md mx-auto bg-white shadow-lg rounded-lg animate-fadeIn">
        <CardHeader className="text-center">
          <Image src="/fitfocus-logo.png" alt="FitFocus Logo" width={100} height={100} className="mx-auto mb-4" />
          <CardTitle className="text-3xl font-bold text-indigo-600">FitFocus Quick Poll</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-center mb-6 text-gray-700">What's your favorite type of workout?</p>
          <div className="space-y-4">
            {workoutOptions.map(({ name, icon }) => {
              const voteCount = votes[name]
              const percentage = totalVotes > 0 ? ((voteCount / totalVotes) * 100).toFixed(1) : "0.0"
              return (
                <div key={name} className="space-y-2">
                  <div className="flex items-center justify-between">
                    <Button
                      variant={userVoted ? "ghost" : "outline"}
                      className={`flex items-center w-full justify-start transition-transform transform hover:scale-105 ${
                        userVoted ? "cursor-not-allowed" : ""
                      }`}
                      onClick={() => handleVote(name)}
                      disabled={userVoted}
                    >
                      {icon}
                      <span className="ml-2">{name}</span>
                    </Button>
                    <span className="text-sm font-medium text-gray-600">{percentage}%</span>
                  </div>
                  <Progress
                    value={parseFloat(percentage)}
                    className="h-3 rounded-full transition-all duration-500 ease-out"
                  />
                </div>
              )
            })}
          </div>
          <p className="text-center mt-6 text-sm text-gray-500">
            Total votes: {totalVotes}
          </p>
          {userVoted && (
            <p className="text-center mt-2 text-green-600 text-sm">
              Thank you for voting!
            </p>
          )}
        </CardContent>
      </Card>
    </div>
  )
}

"use client"

import { useState } from "react"
import { Star, Image as ImageIcon, Send } from "lucide-react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { Textarea } from "@/components/ui/textarea"
import { Tooltip } from "@/components/ui/tooltip"
import Image from "next/image"

const initialRating = {
  average: 4.2,
  count: 156
}

export default function RecipeRating() {
  const [rating, setRating] = useState(0)
  const [hover, setHover] = useState(0)
  const [averageRating, setAverageRating] = useState(initialRating.average)
  const [ratingCount, setRatingCount] = useState(initialRating.count)
  const [comment, setComment] = useState("")
  const [isSubmitting, setIsSubmitting] = useState(false)

  const handleRating = (rate: number) => {
    setRating(rate)
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (rating > 0) {
      setIsSubmitting(true)
      // Simulate API call
      setTimeout(() => {
        console.log(`Submitted rating: ${rating}`)
        console.log(`Submitted comment: ${comment}`)

        const newCount = ratingCount + 1
        const newAverage = (averageRating * ratingCount + rating) / newCount
        setAverageRating(Number(newAverage.toFixed(1)))
        setRatingCount(newCount)

        setRating(0)
        setComment("")
        setIsSubmitting(false)
      }, 1000)
    }
  }

  return (
    <Card className="w-full max-w-md mx-auto shadow-lg rounded-lg overflow-hidden">
      <CardHeader className="relative">
        <Image
          src="/recipe.jpg"
          alt="Delicious Recipe"
          width={400}
          height={200}
          className="w-full h-48 object-cover"
        />
        <div className="absolute bottom-0 left-0 bg-black bg-opacity-50 text-white w-full text-center py-2">
          <CardTitle className="text-xl">Delicious Chocolate Cake</CardTitle>
        </div>
      </CardHeader>
      <CardContent>
        <div className="flex flex-col items-center space-y-4">
          <div className="flex items-center space-x-2">
            {[1, 2, 3, 4, 5].map((star) => (
              <Tooltip key={star} content={`Rate ${star} star${star > 1 ? 's' : ''}`}>
                <Star
                  className={`w-10 h-10 cursor-pointer transition-transform transform ${
                    (hover || rating) >= star
                      ? "fill-yellow-400 text-yellow-400 scale-110"
                      : "text-gray-300 hover:text-yellow-400"
                  }`}
                  onClick={() => handleRating(star)}
                  onMouseEnter={() => setHover(star)}
                  onMouseLeave={() => setHover(0)}
                />
              </Tooltip>
            ))}
          </div>
          <div className="text-lg font-semibold flex items-center space-x-2">
            <Star className="w-5 h-5 text-yellow-400" />
            <span>Average Rating: {averageRating} ({ratingCount} ratings)</span>
          </div>
          <div className="w-full">
            <Textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Leave a comment..."
              className="mt-2"
            />
          </div>
        </div>
      </CardContent>
      <CardFooter>
        <form onSubmit={handleSubmit} className="w-full flex flex-col space-y-2">
          <Button
            type="submit"
            className="w-full flex items-center justify-center transition-transform transform hover:scale-105"
            disabled={rating === 0 || isSubmitting}
          >
            {isSubmitting ? "Submitting..." : (
              <>
                <Send className="w-5 h-5 mr-2" />
                Submit Rating
              </>
            )}
          </Button>
          <div className="flex justify-center space-x-4">
            <Button variant="outline" size="sm">
              Share
            </Button>
            <Button variant="ghost" size="sm">
              Save
            </Button>
          </div>
        </form>
      </CardFooter>
    </Card>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Button } from "@/components/ui/button"
import { ChevronLeft, ChevronRight, Star } from "lucide-react"

const testimonials = [
  {
    id: 1,
    name: "Sarah Johnson",
    photo: "/placeholder.svg?height=100&width=100",
    text: "Emily's photography captured the essence of our wedding day beautifully. Her attention to detail is impeccable!",
    rating: 5,
  },
  {
    id: 2,
    name: "Michael Chen",
    photo: "/placeholder.svg?height=100&width=100",
    text: "I was blown away by the quality of Emily's corporate headshots. She made everyone feel comfortable and look their best.",
    rating: 5,
  },
  {
    id: 3,
    name: "Olivia Rodriguez",
    photo: "/placeholder.svg?height=100&width=100",
    text: "Emily has a gift for capturing genuine moments. Our family photos are treasures we'll cherish forever.",
    rating: 5,
  },
]

export default function TestimonialSlider() {
  const [currentIndex, setCurrentIndex] = useState(0)
  const [isHovered, setIsHovered] = useState(false)
  const [fade, setFade] = useState(true)

  const nextTestimonial = () => {
    setFade(false)
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % testimonials.length)
      setFade(true)
    }, 300)
  }

  const prevTestimonial = () => {
    setFade(false)
    setTimeout(() => {
      setCurrentIndex((prevIndex) => (prevIndex - 1 + testimonials.length) % testimonials.length)
      setFade(true)
    }, 300)
  }

  const goToTestimonial = (index) => {
    if (index === currentIndex) return
    setFade(false)
    setTimeout(() => {
      setCurrentIndex(index)
      setFade(true)
    }, 300)
  }

  useEffect(() => {
    if (isHovered) return
    const timer = setInterval(nextTestimonial, 5000)
    return () => clearInterval(timer)
  }, [currentIndex, isHovered])

  const currentTestimonial = testimonials[currentIndex]

  return (
    <div
      className="w-full max-w-4xl mx-auto p-6 bg-gradient-to-r from-indigo-50 to-purple-50 rounded-lg shadow-2xl relative overflow-hidden"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <svg
        className="absolute top-0 left-0 w-32 h-32 text-indigo-200 opacity-50"
        fill="currentColor"
        viewBox="0 0 100 100"
      >
        <circle cx="50" cy="50" r="50" />
      </svg>
      <svg
        className="absolute bottom-0 right-0 w-32 h-32 text-purple-200 opacity-50"
        fill="currentColor"
        viewBox="0 0 100 100"
      >
        <circle cx="50" cy="50" r="50" />
      </svg>
      <h2 className="text-3xl font-extrabold text-center mb-8 text-gray-800">What Our Clients Say</h2>
      <div className={`transition-opacity duration-500 ${fade ? 'opacity-100' : 'opacity-0'}`}>
        <div className="flex flex-col md:flex-row items-center gap-6 p-4">
          <Avatar className="w-24 h-24">
            <AvatarImage src={currentTestimonial.photo} alt={currentTestimonial.name} />
            <AvatarFallback>{currentTestimonial.name.split(' ').map(n => n[0]).join('')}</AvatarFallback>
          </Avatar>
          <div className="flex-1 text-center md:text-left">
            <p className="text-lg italic mb-4 text-gray-700">&ldquo;{currentTestimonial.text}&rdquo;</p>
            <div className="flex items-center justify-center md:justify-start mb-2">
              {[...Array(5)].map((_, i) => (
                <Star key={i} className={`h-5 w-5 ${i < currentTestimonial.rating ? 'text-yellow-400' : 'text-gray-300'}`} />
              ))}
            </div>
            <p className="font-semibold text-indigo-600">{currentTestimonial.name}</p>
          </div>
        </div>
      </div>
      <div className="absolute top-1/2 transform -translate-y-1/2 left-4 md:left-6">
        <Button
          variant="ghost"
          size="icon"
          className="rounded-full hover:bg-indigo-100 transition transform hover:scale-110"
          onClick={prevTestimonial}
          aria-label="Previous testimonial"
        >
          <ChevronLeft className="h-6 w-6 text-indigo-600" />
        </Button>
      </div>
      <div className="absolute top-1/2 transform -translate-y-1/2 right-4 md:right-6">
        <Button
          variant="ghost"
          size="icon"
          className="rounded-full hover:bg-indigo-100 transition transform hover:scale-110"
          onClick={nextTestimonial}
          aria-label="Next testimonial"
        >
          <ChevronRight className="h-6 w-6 text-indigo-600" />
        </Button>
      </div>
      <div className="flex justify-center mt-6 gap-3">
        {testimonials.map((_, index) => (
          <button
            key={index}
            className={`h-3 w-3 rounded-full focus:outline-none transition ${
              index === currentIndex ? 'bg-indigo-600' : 'bg-gray-300 hover:bg-indigo-400'
            }`}
            onClick={() => goToTestimonial(index)}
            aria-label={`Go to testimonial ${index + 1}`}
          />
        ))}
      </div>
    </div>
  )
}

'use client'

import { Progress } from "@/components/ui/progress"
import { Dumbbell, RefreshCw, TrendingUp } from "lucide-react"
import { useEffect, useState } from "react"
import { Card, CardHeader, CardBody, CardFooter } from "@/components/ui/card"
import { Button } from "@/components/ui/button"

interface ProgressCircleProps {
  progress: number
  size?: 'sm' | 'md' | 'lg'
}

export default function Component({ progress: initialProgress = 0, size = 'md' }: ProgressCircleProps) {
  const [progress, setProgress] = useState(0)
  const [isAnimating, setIsAnimating] = useState(false)

  useEffect(() => {
    setIsAnimating(true)
    const timer = setTimeout(() => setProgress(initialProgress), 500)
    return () => clearTimeout(timer)
  }, [initialProgress])

  const sizeClasses = {
    sm: 'w-24 h-24',
    md: 'w-32 h-32',
    lg: 'w-40 h-40'
  }

  const handleReset = () => {
    setIsAnimating(true)
    setProgress(0)
  }

  const handleIncrease = () => {
    setIsAnimating(true)
    setProgress(prev => (prev + 10 > 100 ? 100 : prev + 10))
  }

  const handleDecrease = () => {
    setIsAnimating(true)
    setProgress(prev => (prev - 10 < 0 ? 0 : prev - 10))
  }

  return (
