"use client"

import { useState, useRef, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Slider } from "@/components/ui/slider"
import { Volume2, VolumeX, SkipBack, SkipForward, Play, Pause, Shuffle, Repeat, Heart } from "lucide-react"
import { Card, CardHeader, CardBody, CardFooter } from "@/components/ui/card"
import { Transition } from "@headlessui/react"

export default function MusicPlayer() {
  const [isPlaying, setIsPlaying] = useState(false)
  const [currentTime, setCurrentTime] = useState(0)
  const [duration, setDuration] = useState(0)
  const [volume, setVolume] = useState(1)
  const [isMuted, setIsMuted] = useState(false)
  const [playlist] = useState([
    {
      title: "Neon Lights",
      artist: "The Electron Band",
      album: "Digital Dreams",
      artwork: "/placeholder.svg?height=400&width=400",
      audioSrc: "/path-to-audio-file1.mp3"
    },
    {
      title: "Synth Wave",
      artist: "Retro Beats",
      album: "Night Drive",
      artwork: "/placeholder2.svg?height=400&width=400",
      audioSrc: "/path-to-audio-file2.mp3"
    },
    {
      title: "Electric Pulse",
      artist: "Future Sounds",
      album: "Urban Echoes",
      artwork: "/placeholder3.svg?height=400&width=400",
      audioSrc: "/path-to-audio-file3.mp3"
    },
  ])
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0)
  const [isShuffle, setIsShuffle] = useState(false)
  const [repeatMode, setRepeatMode] = useState<'off' | 'all' | 'one'>('off')
  const [isFavorite, setIsFavorite] = useState(false)
  const audioRef = useRef<HTMLAudioElement | null>(null)

  const currentTrack = playlist[currentTrackIndex]

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.addEventListener('loadedmetadata', () => {
        setDuration(audioRef.current!.duration)
      })
      audioRef.current.addEventListener('timeupdate', () => {
        setCurrentTime(audioRef.current!.currentTime)
      })
      audioRef.current.addEventListener('ended', handleTrackEnd)
    }

    return () => {
      if (audioRef.current) {
        audioRef.current.removeEventListener('ended', handleTrackEnd)
      }
    }
  }, [currentTrackIndex, isShuffle, repeatMode])

  const handleTrackEnd = () => {
    if (repeatMode === 'one') {
      audioRef.current!.currentTime = 0
      audioRef.current!.play()
    } else if (isShuffle) {
      playShuffle()
    } else if (currentTrackIndex < playlist.length - 1) {
      setCurrentTrackIndex(currentTrackIndex + 1)
    } else if (repeatMode === 'all') {
      setCurrentTrackIndex(0)
    } else {
      setIsPlaying(false)
    }
  }

  const playShuffle = () => {
    const nextIndex = Math.floor(Math.random() * playlist.length)
    setCurrentTrackIndex(nextIndex)
  }

  const togglePlayPause = () => {
    if (audioRef.current) {
      if (audioRef.current.paused) {
        audioRef.current.play()
        setIsPlaying(true)
      } else {
        audioRef.current.pause()
        setIsPlaying(false)
      }
    }
  }

  const handleVolumeChange = (newVolume: number[]) => {
    const volumeValue = newVolume[0]
    setVolume(volumeValue)
    if (audioRef.current) {
      audioRef.current.volume = volumeValue
      setIsMuted(volumeValue === 0)
    }
  }

  const toggleMute = () => {
    if (audioRef.current) {
      if (isMuted) {
        audioRef.current.volume = volume
        setIsMuted(false)
      } else {
        audioRef.current.volume = 0
        setIsMuted(true)
      }
    }
  }

  const handleSeek = (newTime: number[]) => {
    const timeValue = newTime[0]
    if (audioRef.current) {
      audioRef.current.currentTime = timeValue
      setCurrentTime(timeValue)
    }
  }

  const formatTime = (time: number) => {
    const minutes = Math.floor(time / 60)
    const seconds = Math.floor(time % 60)
    return `${minutes}:${seconds.toString().padStart(2, '0')}`
  }

  const handleNextTrack = () => {
    if (isShuffle) {
      playShuffle()
    } else {
      if (currentTrackIndex < playlist.length - 1) {
        setCurrentTrackIndex(currentTrackIndex + 1)
      } else if (repeatMode === 'all') {
        setCurrentTrackIndex(0)
      }
    }
  }

  const handlePrevTrack = () => {
    if (isShuffle) {
      playShuffle()
    } else {
      if (currentTrackIndex > 0) {
        setCurrentTrackIndex(currentTrackIndex - 1)
      } else if (repeatMode === 'all') {
        setCurrentTrackIndex(playlist.length - 1)
      }
    }
  }

  const toggleShuffle = () => {
    setIsShuffle(!isShuffle)
  }

  const toggleRepeat = () => {
    if (repeatMode === 'off') {
      setRepeatMode('all')
    } else if (repeatMode === 'all') {
      setRepeatMode('one')
    } else {
      setRepeatMode('off')
    }
  }

  const toggleFavorite = () => {
    setIsFavorite(!isFavorite)
  }

  return (
    <div className="w-full max-w-md mx-auto bg-gradient-to-r from-indigo-600 to-purple-600 shadow-xl rounded-lg overflow-hidden transform transition-transform duration-500 hover:scale-105">
      <Card className="bg-white bg-opacity-10 backdrop-filter backdrop-blur-lg">
        <CardHeader className="p-4 flex flex-col items-center">
          <img
            src={currentTrack.artwork}
            alt={`${currentTrack.album} cover`}
            className="w-40 h-40 object-cover rounded-full shadow-md mb-4 animate-spin-slow"
          />
          <h2 className="text-3xl font-bold text-white">{currentTrack.title}</h2>
          <p className="text-gray-200">{currentTrack.artist} - {currentTrack.album}</p>
        </CardHeader>
        <CardBody className="p-4">
          <div className="flex items-center justify-between mb-4">
            <span className="text-sm text-gray-300">{formatTime(currentTime)}</span>
            <Slider
              max={duration}
              value={[currentTime]}
              onValueChange={handleSeek}
              className="w-full mx-4"
              aria-label="Seek time"
            />
            <span className="text-sm text-gray-300">{formatTime(duration)}</span>
          </div>
          <div className="flex justify-center space-x-4 mb-4">
            <Button variant={isShuffle ? "default" : "ghost"} size="icon" onClick={toggleShuffle} aria-label="Shuffle">
              <Shuffle className={`h-5 w-5 ${isShuffle ? 'text-indigo-400' : 'text-gray-300'} transition-colors duration-300`} />
            </Button>
            <Button variant="ghost" size="icon" onClick={handlePrevTrack} aria-label="Previous track" className="transform transition-transform duration-300 hover:scale-110">
              <SkipBack className="h-6 w-6 text-gray-300" />
            </Button>
            <Button onClick={togglePlayPause} variant="default" size="lg" className="h-12 w-12 rounded-full bg-indigo-500 hover:bg-indigo-600 focus:ring-2 focus:ring-indigo-400 transition-colors duration-300" aria-label={isPlaying ? "Pause" : "Play"}>
              <Transition
                show={isPlaying}
                enter="transition-opacity duration-300"
                enterFrom="opacity-0"
                enterTo="opacity-100"
                leave="transition-opacity duration-300"
                leaveFrom="opacity-100"
                leaveTo="opacity-0"
              >
                <Pause className="h-6 w-6 text-white" />
              </Transition>
              <Transition
                show={!isPlaying}
                enter="transition-opacity duration-300"
                enterFrom="opacity-0"
                enterTo="opacity-100"
                leave="transition-opacity duration-300"
                leaveFrom="opacity-100"
                leaveTo="opacity-0"
              >
                <Play className="h-6 w-6 text-white" />
              </Transition>
            </Button>
            <Button variant="ghost" size="icon" onClick={handleNextTrack} aria-label="Next track" className="transform transition-transform duration-300 hover:scale-110">
              <SkipForward className="h-6 w-6 text-gray-300" />
            </Button>
            <Button variant={repeatMode !== 'off' ? "default" : "ghost"} size="icon" onClick={toggleRepeat} aria-label="Repeat">
              <Repeat className={`h-5 w-5 ${repeatMode !== 'off' ? 'text-indigo-400' : 'text-gray-300'} transition-colors duration-300`} />
            </Button>
          </div>
          <div className="flex items-center">
            <Button variant="ghost" size="icon" onClick={toggleMute} aria-label={isMuted ? "Unmute" : "Mute"}>
              {isMuted ? <VolumeX className="h-6 w-6 text-gray-300" /> : <Volume2 className="h-6 w-6 text-gray-300" />}
            </Button>
            <Slider
              max={1}
              step={0.01}
              value={[isMuted ? 0 : volume]}
              onValueChange={handleVolumeChange}
              className="w-full mx-4"
              aria-label="Adjust volume"
            />
            <Button variant="ghost" size="icon" onClick={toggleFavorite} aria-label="Favorite">
              <Heart className={`h-6 w-6 ${isFavorite ? 'text-red-500' : 'text-gray-300'} transition-colors duration-300`} />
            </Button>
          </div>
        </CardBody>
        <CardFooter className="p-4">
          <ul className="space-y-2">
            {playlist.map((track, index) => (
              <li key={index} className={`flex items-center p-2 rounded-lg cursor-pointer hover:bg-indigo-500 ${index === currentTrackIndex ? 'bg-indigo-700' : 'bg-transparent'}`} onClick={() => setCurrentTrackIndex(index)}>
                <img src={track.artwork} alt={`${track.album} cover`} className="w-10 h-10 object-cover rounded mr-4" />
                <div>
                  <p className="text-white">{track.title}</p>
                  <p className="text-gray-300 text-sm">{track.artist}</p>
                </div>
              </li>
            ))}
          </ul>
        </CardFooter>
      </Card>
      <audio ref={audioRef} src={currentTrack.audioSrc} />
    </div>
}

import { Button } from "@/components/ui/button"
import { Download } from "lucide-react"
import { Card } from "@/components/ui/card"
import Image from "next/image"
import { useState } from "react"

export default function Component() {
  const [isDownloading, setIsDownloading] = useState(false)

  const handleDownload = () => {
    setIsDownloading(true)
    // Replace this URL with the actual URL of your resume PDF
    const resumeUrl = "/path-to-your-resume.pdf"

    // Create a temporary anchor element
    const link = document.createElement("a")
    link.href = resumeUrl
    link.download = "InspireNow_Resume.pdf"
    document.body.appendChild(link)

    // Trigger the download
    link.click()

    // Clean up
    document.body.removeChild(link)
    setIsDownloading(false)
  }

  return (
    <div className="flex justify-center items-center min-h-screen bg-gradient-to-r from-purple-500 to-pink-500 p-4">
      <Card className="max-w-sm w-full p-6 bg-white rounded-lg shadow-lg transform transition-transform hover:scale-105">
        <div className="flex flex-col items-center">
          <Image
            src="/profile-picture.jpg"
            alt="Profile Picture"
            width={100}
            height={100}
            className="rounded-full mb-4"
          />
          <h2 className="text-2xl font-semibold text-gray-800 mb-2">John Doe</h2>
          <p className="text-gray-600 mb-4 text-center">
            Frontend Engineer & UI/UX Designer
          </p>
          <Button
            onClick={handleDownload}
            disabled={isDownloading}
            className="bg-purple-600 text-white hover:bg-purple-700 transition-colors duration-300 font-semibold py-2 px-4 rounded-full shadow-md flex items-center space-x-2"
          >
            <Download className="w-5 h-5" />
            <span>{isDownloading ? "Downloading..." : "Download Resume"}</span>
          </Button>
        </div>
      </Card>
    </div>
  )
}

"use client"

import { useState, useEffect } from 'react'
import { ethers } from 'ethers'
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card"
import { Label } from "@/components/ui/label"
import { toast, Toaster } from "@/components/ui/toaster"
import { motion } from 'framer-motion'
import Image from 'next/image'
import { Tooltip } from "@/components/ui/tooltip"

const tokenHubABI = [
  "function mint(address to, uint256 amount) public",
  "function balanceOf(address account) public view returns (uint256)",
  "function symbol() public view returns (string)"
]

const contractAddress = "YOUR_CONTRACT_ADDRESS_HERE"

export default function TokenHub() {
  const [provider, setProvider] = useState<ethers.providers.Web3Provider | null>(null)
  const [signer, setSigner] = useState<ethers.Signer | null>(null)
  const [contract, setContract] = useState<ethers.Contract | null>(null)
  const [address, setAddress] = useState<string>("")
  const [balance, setBalance] = useState<string>("0")
  const [amount, setAmount] = useState<string>("")
  const [symbol, setSymbol] = useState<string>("")

  useEffect(() => {
    const init = async () => {
      if (typeof window.ethereum !== 'undefined') {
        const web3Provider = new ethers.providers.Web3Provider(window.ethereum)
        setProvider(web3Provider)

        const tokenHubContract = new ethers.Contract(contractAddress, tokenHubABI, web3Provider)
        setContract(tokenHubContract)

        const tokenSymbol = await tokenHubContract.symbol()
        setSymbol(tokenSymbol)
      } else {
        toast({
          title: 'MetaMask Not Detected',
          description: 'Please install MetaMask to use TokenHub.',
          variant: 'destructive',
        })
      }
    }

    init()
  }, [])

  const connectWallet = async () => {
    if (provider) {
      try {
        await provider.send("eth_requestAccounts", [])
        const signer = provider.getSigner()
        setSigner(signer)
        const address = await signer.getAddress()
        setAddress(address)
        if (contract) {
          const balance = await contract.balanceOf(address)
          setBalance(ethers.utils.formatEther(balance))
        }
        toast({
          title: 'Wallet Connected',
          description: `Address: ${address}`,
          variant: 'success',
        })
      } catch (error) {
        toast({
          title: 'Connection Failed',
          description: 'Unable to connect wallet.',
          variant: 'destructive',
        })
        console.error(error)
      }
    }
  }

  const mintTokens = async () => {
    if (signer && contract) {
      try {
        const tx = await contract.connect(signer).mint(address, ethers.utils.parseEther(amount))
        toast({
          title: 'Minting Tokens',
          description: 'Please wait while your transaction is being processed.',
          variant: 'default',
        })
        await tx.wait()
        const newBalance = await contract.balanceOf(address)
        setBalance(ethers.utils.formatEther(newBalance))
        setAmount("")
        toast({
          title: 'Tokens Minted',
          description: `Successfully minted ${amount} ${symbol}.`,
          variant: 'success',
        })
      } catch (error) {
        toast({
          title: 'Minting Failed',
          description: 'There was an error minting your tokens.',
          variant: 'destructive',
        })
        console.error("Error minting tokens:", error)
      }
    }
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="min-h-screen flex items-center justify-center bg-gradient-to-r from-indigo-500 to-purple-600 p-4"
    >
      <Toaster />
      <Card className="w-full max-w-md bg-white dark:bg-gray-800 shadow-2xl rounded-xl overflow-hidden">
        <CardHeader className="flex flex-col items-center">
          <Image src="/tokenhub-logo.svg" alt="TokenHub Logo" width={100} height={100} className="mb-4" />
          <CardTitle className="text-2xl font-bold text-indigo-600 dark:text-indigo-400">TokenHub</CardTitle>
          <CardDescription className="text-gray-500 dark:text-gray-300">Mint and manage your own tokens</CardDescription>
        </CardHeader>
        <CardContent className="mt-4">
          {!address ? (
            <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
              <Tooltip content="Connect your Ethereum wallet to get started">
                <Button onClick={connectWallet} className="w-full bg-indigo-600 hover:bg-indigo-700 transition-colors duration-300">
                  Connect Wallet
                </Button>
              </Tooltip>
            </motion.div>
          ) : (
            <div className="space-y-6">
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.2 }}
                className="flex flex-col space-y-2"
              >
                <div className="flex justify-between">
                  <span className="text-gray-700 dark:text-gray-200">Address:</span>
                  <span className="font-mono text-sm text-gray-900 dark:text-gray-100 truncate max-w-[150px]">
                    {address}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-700 dark:text-gray-200">Balance:</span>
                  <span className="text-gray-900 dark:text-gray-100">{balance} {symbol}</span>
                </div>
              </motion.div>
              <div className="space-y-1">
                <Label htmlFor="amount" className="text-gray-700 dark:text-gray-200">Amount to Mint</Label>
                <Input
                  id="amount"
                  type="number"
                  placeholder="Enter amount"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                  className="transition-all focus:ring-2 focus:ring-indigo-500"
                />
              </div>
            </div>
          )}
        </CardContent>
        {address && (
          <CardFooter>
            <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
              <Button
                onClick={mintTokens}
                disabled={!address || !amount}
                className="w-full bg-purple-600 hover:bg-purple-700 transition-colors duration-300 disabled:opacity-50"
              >
                Mint Tokens
              </Button>
            </motion.div>
          </CardFooter>
        )}
      </Card>
    </motion.div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Heart, Trash2, Plus, Book, Star } from 'lucide-react'
import { motion, AnimatePresence } from 'framer-motion'

interface Book {
  id: string
  title: string
  author: string
  image?: string
}

export default function Wishlist() {
  const [wishlist, setWishlist] = useState<Book[]>([])
  const [newBook, setNewBook] = useState<Book>({ id: '', title: '', author: '', image: '' })
  const [isAdding, setIsAdding] = useState(false)

  useEffect(() => {
    const storedWishlist = localStorage.getItem('bookNestWishlist')
    if (storedWishlist) {
      setWishlist(JSON.parse(storedWishlist))
    }
  }, [])

  useEffect(() => {
    localStorage.setItem('bookNestWishlist', JSON.stringify(wishlist))
  }, [wishlist])

  const addToWishlist = () => {
    if (newBook.title && newBook.author) {
      setIsAdding(true)
      const bookToAdd = {
        ...newBook,
        id: Date.now().toString(),
        image: newBook.image || `https://via.placeholder.com/150?text=${encodeURIComponent(newBook.title)}`
      }
      setWishlist([...wishlist, bookToAdd])
      setNewBook({ id: '', title: '', author: '', image: '' })
      setTimeout(() => setIsAdding(false), 500)
    }
  }

  const removeFromWishlist = (id: string) => {
    if (confirm('Are you sure you want to remove this book from your wishlist?')) {
      setWishlist(wishlist.filter(book => book.id !== id))
    }
  }

  const clearWishlist = () => {
    if (confirm('Are you sure you want to clear your entire wishlist?')) {
      setWishlist([])
    }
  }

  return (
    <div className="container mx-auto p-4">
      <header className="flex flex-col items-center mb-8">
        <h1 className="text-4xl font-extrabold text-indigo-600 flex items-center">
          <Book className="mr-2" />
          BookNest Wishlist
        </h1>
        <p className="text-gray-600 mt-2">Save and manage your favorite books for future purchase</p>
      </header>

      <Card className="mb-6 shadow-lg transform transition-transform hover:scale-105">
        <CardHeader>
          <CardTitle>Add a Book to Your Wishlist</CardTitle>
          <CardDescription>Enter the details of the book you'd like to add.</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid w-full gap-4">
            <div className="flex flex-col space-y-1.5">
              <Label htmlFor="title">Title</Label>
              <Input 
                id="title" 
                placeholder="Enter book title"
                value={newBook.title}
                onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
                className="focus:border-indigo-500"
              />
            </div>
            <div className="flex flex-col space-y-1.5">
              <Label htmlFor="author">Author</Label>
              <Input 
                id="author" 
                placeholder="Enter author name"
                value={newBook.author}
                onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
                className="focus:border-indigo-500"
              />
            </div>
            <div className="flex flex-col space-y-1.5">
              <Label htmlFor="image">Cover Image URL</Label>
              <Input 
                id="image" 
                placeholder="Enter image URL (optional)"
                value={newBook.image}
                onChange={(e) => setNewBook({ ...newBook, image: e.target.value })}
                className="focus:border-indigo-500"
              />
            </div>
          </div>
        </CardContent>
        <CardFooter className="flex justify-end">
          <Button 
            onClick={addToWishlist} 
            disabled={!newBook.title || !newBook.author || isAdding}
            className="flex items-center"
          >
            <Plus className="mr-2 h-4 w-4" />
            {isAdding ? 'Adding...' : 'Add to Wishlist'}
          </Button>
        </CardFooter>
      </Card>

      {wishlist.length > 0 && (
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold flex items-center">
            <Star className="mr-2 text-yellow-500" />
            Your Wishlist
          </h2>
          <Button variant="destructive" size="icon" onClick={clearWishlist}>
            Clear All
          </Button>
        </div>
      )}

      <AnimatePresence>
        <motion.div 
          className="grid gap-6 md:grid-cols-2 lg:grid-cols-3"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          {wishlist.map(book => (
            <motion.div 
              key={book.id}
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              transition={{ duration: 0.3 }}
            >
              <Card className="hover:shadow-2xl transition-shadow">
                <CardHeader className="flex items-center">
                  <img 
                    src={book.image} 
                    alt={`${book.title} cover`} 
                    className="w-16 h-24 object-cover rounded-md mr-4"
                  />
                  <div>
                    <CardTitle className="text-lg">{book.title}</CardTitle>
                    <CardDescription className="text-gray-500">{book.author}</CardDescription>
                  </div>
                </CardHeader>
                <CardFooter className="flex justify-end space-x-2">
                  <Button variant="secondary" size="icon" aria-label="Favorite">
                    <Heart className="h-4 w-4 text-red-500" />
                  </Button>
                  <Button variant="destructive" size="icon" aria-label="Remove" onClick={() => removeFromWishlist(book.id)}>
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </CardFooter>
              </Card>
            </motion.div>
          ))}
        </motion.div>
      </AnimatePresence>

      {wishlist.length === 0 && (
        <div className="flex flex-col items-center mt-10">
          <Book className="h-12 w-12 text-gray-300 mb-4" />
          <p className="text-gray-500">Your wishlist is empty. Start adding some books!</p>
        </div>
      )}
    </div>
  )
}

