import { Tooltip } from "@/components/ui/tooltip"
import Image from "next/image"
import { motion } from "framer-motion"

export default function StakingRewardsCounter() {
  const [rewards, setRewards] = useState(0)
  const [isStaking, setIsStaking] = useState(true)
  const [progressValue, setProgressValue] = useState(0)
  const [maxRewards] = useState(100)
  const rewardsPerSecond = 0.01

  useEffect(() => {
    let interval: NodeJS.Timeout

    if (isStaking) {
      interval = setInterval(() => {
        setRewards((prevRewards) => {
          const newRewards = prevRewards + rewardsPerSecond
          setProgressValue((newRewards / maxRewards) * 100)
          return newRewards > maxRewards ? maxRewards : newRewards
        })
      }, 1000)
    }

    return () => clearInterval(interval)
  }, [isStaking, rewardsPerSecond, maxRewards])

  const formatRewards = (value: number) => value.toFixed(2)

  const handleClaim = () => {
    alert(`Claimed ${formatRewards(rewards)} tokens!`)
    setRewards(0)
    setProgressValue(0)
  }

  const toggleStaking = () => setIsStaking(!isStaking)

  return (
    <Card className="w-full max-w-sm mx-auto shadow-2xl transition-transform transform hover:scale-105">
      <CardHeader className="bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 text-white rounded-t-lg">
        <CardTitle className="text-3xl font-extrabold flex items-center">
          <Coins className="w-8 h-8 mr-2 animate-bounce" />
          Staking Rewards
        </CardTitle>
        <CardDescription className="text-sm">Earn and track your rewards in real-time</CardDescription>
      </CardHeader>
      <CardContent className="flex flex-col items-center p-6 bg-gray-50">
        <div className="relative w-40 h-40">
          <motion.svg
            className="w-40 h-40 animate-spin-slow"
            viewBox="0 0 100 100"
          >
            <circle
              cx="50"
              cy="50"
              r="45"
              stroke="#e5e7eb"
              strokeWidth="10"
              fill="none"
              className="opacity-20"
            />
            <motion.circle
              cx="50"
              cy="50"
              r="45"
              stroke="#6366f1"
              strokeWidth="10"
              fill="none"
              strokeDasharray={2 * Math.PI * 45}
              strokeDashoffset={2 * Math.PI * 45 * ((100 - progressValue) / 100)}
              strokeLinecap="round"
              animate={{ strokeDashoffset: 2 * Math.PI * 45 * ((100 - progressValue) / 100) }}
              transition={{ duration: 1, ease: "easeInOut" }}
            />
            <div className="absolute inset-0 flex items-center justify-center">
              <Coins className="w-12 h-12 text-yellow-400 animate-pulse" />
            </div>
          </motion.svg>
        </div>
        <motion.div
          className="mt-6 text-5xl font-bold text-indigo-600"
          animate={{ number: rewards }}
          transition={{ duration: 1 }}
        >
          {formatRewards(rewards)}
        </motion.div>
        <div className="text-sm text-gray-500">Tokens Earned</div>
        <Progress value={progressValue} className="w-full mt-4" />
        <div className="flex justify-between w-full mt-4 text-sm text-gray-600">
          <div>
            <span className="font-medium">APY:</span> 12.5%
          </div>
          <div>
            <span className="font-medium">Total Staked:</span> 1,000 Tokens
          </div>
        </div>
      </CardContent>
      <CardFooter className="flex flex-col space-y-2 p-6 bg-gray-100 rounded-b-lg">
        <Tooltip content="Claim your accumulated rewards">
          <Button
            onClick={handleClaim}
            disabled={rewards === 0}
            className="flex items-center justify-center space-x-2 transition-colors duration-300 hover:bg-indigo-600"
          >
            <Check className="w-4 h-4" />
            Claim Rewards
          </Button>
        </Tooltip>
        <Tooltip content={isStaking ? "Pause staking to stop earning" : "Resume staking to continue earning"}>
          <Button
            variant="outline"
            onClick={toggleStaking}
            className="flex items-center justify-center space-x-2 transition-colors duration-300 hover:border-indigo-600"
          >
            {isStaking ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
            {isStaking ? "Pause" : "Resume"}
          </Button>
        </Tooltip>
      </CardFooter>
      <style jsx>{`
        @keyframes spin-slow {
          from {
            transform: rotate(0deg);
          }
          to {
            transform: rotate(360deg);
          }
        }
        .animate-spin-slow {
          animation: spin-slow 20s linear infinite;
        }
      `}</style>
    </Card>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { ethers } from 'ethers'
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card"
import { socialNodeChain } from '@/lib/socialNodeChain'
import { motion } from 'framer-motion'
import { Tooltip } from "@/components/ui/tooltip"
import { Loader } from "@/components/ui/loader"
import { ToastProvider, toast } from "@/components/ui/toast"

export default function SocialNodeApp() {
  const [posts, setPosts] = useState([])
  const [newPost, setNewPost] = useState('')
  const [account, setAccount] = useState('')
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    loadBlockchainData()
  }, [])

  async function loadBlockchainData() {
    if (typeof window.ethereum === 'undefined') {
      toast({
        title: "Wallet Not Found",
        description: "Please install MetaMask to use SocialNode.",
        variant: "destructive",
      })
      return
    }
    setLoading(true)
    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum)
      const signer = provider.getSigner()
      const address = await signer.getAddress()
      setAccount(address)

      // Load posts from the blockchain
      const fetchedPosts = await socialNodeChain.getPosts()
      setPosts(fetchedPosts)
    } catch (error) {
      console.error("Error loading blockchain data:", error)
      toast({
        title: "Error",
        description: "Failed to load blockchain data.",
        variant: "destructive",
      })
    }
    setLoading(false)
  }

  async function connectWallet() {
    if (typeof window.ethereum !== 'undefined') {
      try {
        await window.ethereum.request({ method: 'eth_requestAccounts' })
        loadBlockchainData()
        toast({
          title: "Wallet Connected",
          description: "Your wallet has been successfully connected.",
          variant: "default",
        })
      } catch (error) {
        console.error("User denied account access")
        toast({
          title: "Connection Failed",
          description: "Unable to connect your wallet.",
          variant: "destructive",
        })
      }
    } else {
      toast({
        title: "MetaMask Required",
        description: "Please install MetaMask to use SocialNode.",
        variant: "destructive",
      })
    }
  }

  async function createPost(event) {
    event.preventDefault()
    if (newPost.trim() === '') {
      toast({
        title: "Empty Post",
        description: "Please enter some content before posting.",
        variant: "warning",
      })
      return
    }
    setLoading(true)
    try {
      await socialNodeChain.createPost(newPost)
      setNewPost('')
      // Reload posts after creating a new one
      const updatedPosts = await socialNodeChain.getPosts()
      setPosts(updatedPosts)
      toast({
        title: "Post Created",
        description: "Your post has been successfully created.",
        variant: "default",
      })
    } catch (error) {
      console.error("Error creating post:", error)
      toast({
        title: "Error",
        description: "Failed to create post.",
        variant: "destructive",
      })
    }
    setLoading(false)
  }

  return (
    <ToastProvider>
      <div className="min-h-screen bg-gradient-to-r from-blue-50 to-purple-50 dark:bg-gray-900 transition-colors duration-300">
        <header className="bg-white dark:bg-gray-800 shadow-md">
          <div className="container mx-auto px-4 py-4 flex justify-between items-center">
            <h1 className="text-3xl font-extrabold text-indigo-600 dark:text-indigo-400 transition-colors duration-300">SocialNode</h1>
            {account ? (
              <div className="flex items-center space-x-3">
                <Avatar>
                  <AvatarImage src={`https://avatars.dicebear.com/api/identicon/${account}.svg`} />
                  <AvatarFallback>{account.slice(0, 2)}</AvatarFallback>
                </Avatar>
                <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  {`${account.slice(0, 6)}...${account.slice(-4)}`}
                </span>
                <Button variant="outline" size="sm" onClick={connectWallet}>
                  Refresh
                </Button>
              </div>
            ) : (
              <Button onClick={connectWallet} className="animate-pulse">
                Connect Wallet
              </Button>
            )}
          </div>
        </header>

        <main className="container mx-auto px-4 py-6">
          <form onSubmit={createPost} className="mb-8">
            <div className="flex flex-col sm:flex-row items-center space-y-4 sm:space-y-0 sm:space-x-4">
              <Input
                type="text"
                placeholder="What's on your mind?"
                value={newPost}
                onChange={(e) => setNewPost(e.target.value)}
                className="flex-grow"
              />
              <Button type="submit" disabled={loading}>
                {loading ? <Loader className="w-4 h-4 mr-2" /> : null}
                Post
              </Button>
            </div>
          </form>

          {loading && (
            <div className="flex justify-center my-10">
              <Loader />
            </div>
          )}

          <div className="space-y-6">
            {posts.map((post, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
              >
                <Card className="transition-transform transform hover:-translate-y-1 hover:shadow-lg">
                  <CardHeader className="flex items-center space-x-4">
                    <Avatar>
                      <AvatarImage src={`https://avatars.dicebear.com/api/identicon/${post.author}.svg`} />
                      <AvatarFallback>{post.author.slice(0, 2)}</AvatarFallback>
                    </Avatar>
                    <div>
                      <p className="font-semibold text-gray-800 dark:text-gray-200">
                        {`${post.author.slice(0, 6)}...${post.author.slice(-4)}`}
                      </p>
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        {new Date(post.timestamp * 1000).toLocaleString()}
                      </p>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <p className="text-gray-700 dark:text-gray-300">{post.content}</p>
                  </CardContent>
                  <CardFooter className="flex space-x-4">
                    <Tooltip content="Like">
                      <Button variant="ghost" size="sm" className="flex items-center space-x-1">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-red-500" fill="currentColor" viewBox="0 0 20 20">
                          <path d="M3.172 5.172a4 4 0 015.656 0L10 6.343l1.172-1.171a4 4 0 115.656 5.656L10 17.657l-6.828-6.829a4 4 0 010-5.656z" />
                        </svg>
                        <span>Like</span>
                      </Button>
                    </Tooltip>
                    <Tooltip content="Comment">
                      <Button variant="ghost" size="sm" className="flex items-center space-x-1">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-blue-500" fill="currentColor" viewBox="0 0 20 20">
                          <path d="M18 10c0 3.866-3.582 7-8 7a8.96 8.96 0 01-4-.872V17a1 1 0 11-2 0v-2a1 1 0 011-1h2a8.96 8.96 0 01-.872-4A8.96 8.96 0 0110 2a8.96 8.96 0 014 .872V3a1 1 0 012 0v2a1 1 0 01-1 1h-2a8.96 8.96 0 01.872 4z" />
                        </svg>
                        <span>Comment</span>
                      </Button>
                    </Tooltip>
                    <Tooltip content="Share">
                      <Button variant="ghost" size="sm" className="flex items-center space-x-1">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-green-500" fill="currentColor" viewBox="0 0 20 20">
                          <path d="M15 8a3 3 0 10-2.236 5.764l-4.737 2.368a3 3 0 10-.964.964l2.368 4.737A3 3 0 0012 17a3 3 0 100-6 3 3 0 002.236 1.236l4.737-2.368A3 3 0 0015 8z" />
                        </svg>
                        <span>Share</span>
                      </Button>
                    </Tooltip>
                  </CardFooter>
                </Card>
              </motion.div>
            ))}
          </div>
        </main>
      </div>
    </ToastProvider>
  )
}

