"use client"

import { useEffect, useState } from 'react'

export default function ReadingProgressBar() {
  const [readingProgress, setReadingProgress] = useState(0)

  useEffect(() => {
    const updateReadingProgress = () => {
      const currentProgress = window.scrollY
      const scrollHeight = document.documentElement.scrollHeight - window.innerHeight
      if (scrollHeight) {
        setReadingProgress(Number((currentProgress / scrollHeight).toFixed(2)) * 100)
      }
    }

    window.addEventListener('scroll', updateReadingProgress)

    return () => {
      window.removeEventListener('scroll', updateReadingProgress)
    }
  }, [])

  return (
    <div 
      role="progressbar" 
      aria-valuenow={readingProgress} 
      aria-valuemin={0} 
      aria-valuemax={100} 
      className="fixed top-0 left-0 z-50 w-full h-1 bg-gray-200"
    >
      <div 
        style={{width: `${readingProgress}%`}}
        className="h-full bg-black transition-all duration-300 ease-out"
      />
    </div>
  )
}

import { useState } from 'react'
import { Checkbox } from "@/components/ui/checkbox"
import { Label } from "@/components/ui/label"
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card"

interface Ingredient {
  id: string
  name: string
  amount: string
}

const ingredients: Ingredient[] = [
  { id: '1', name: 'Quinoa', amount: '1 cup' },
  { id: '2', name: 'Cherry tomatoes', amount: '1 cup, halved' },
  { id: '3', name: 'Cucumber', amount: '1/2, diced' },
  { id: '4', name: 'Red onion', amount: '1/4, finely chopped' },
  { id: '5', name: 'Feta cheese', amount: '1/2 cup, crumbled' },
  { id: '6', name: 'Kalamata olives', amount: '1/4 cup, pitted and sliced' },
  { id: '7', name: 'Extra virgin olive oil', amount: '2 tablespoons' },
  { id: '8', name: 'Lemon juice', amount: '2 tablespoons' },
  { id: '9', name: 'Fresh mint leaves', amount: '2 tablespoons, chopped' },
  { id: '10', name: 'Salt', amount: 'to taste' },
  { id: '11', name: 'Black pepper', amount: 'to taste' },
]

export default function IngredientChecklist() {
  const [checkedItems, setCheckedItems] = useState<{ [key: string]: boolean }>({})

  const toggleItem = (id: string) => {
    setCheckedItems(prev => ({ ...prev, [id]: !prev[id] }))
  }

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle className="text-2xl font-bold text-center">HealthyEats Mediterranean Quinoa Salad</CardTitle>
      </CardHeader>
      <CardContent>
        <h2 className="text-xl font-semibold mb-4">Ingredients Checklist</h2>
        <ul className="space-y-2">
          {ingredients.map((ingredient) => (
            <li key={ingredient.id} className="flex items-center space-x-2">
              <Checkbox
                id={ingredient.id}
                checked={checkedItems[ingredient.id] || false}
                onCheckedChange={() => toggleItem(ingredient.id)}
              />
              <Label
                htmlFor={ingredient.id}
                className={`flex-grow ${checkedItems[ingredient.id] ? 'line-through text-gray-500' : ''}`}
              >
                {ingredient.name} - {ingredient.amount}
              </Label>
            </li>
          ))}
        </ul>
      </CardContent>
    </Card>
  )
}

'use client'

import { useState } from 'react'
import { Star } from 'lucide-react'
import { Button } from "@/components/ui/button"
import { Textarea } from "@/components/ui/textarea"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"

interface Review {
  id: number
  author: string
  rating: number
  comment: string
  date: string
}

export default function ProductReviews() {
  const [reviews, setReviews] = useState<Review[]>([
    {
      id: 1,
      author: 'Jane Doe',
      rating: 5,
      comment: 'Absolutely love this product! It has made such a difference in my skincare routine.',
      date: '2023-05-15'
    },
    {
      id: 2,
      author: 'John Smith',
      rating: 4,
      comment: 'Great product, but I wish the packaging was more eco-friendly.',
      date: '2023-05-10'
    }
  ])
  const [newReview, setNewReview] = useState({ rating: 0, comment: '' })

  const handleStarClick = (rating: number) => {
    setNewReview(prev => ({ ...prev, rating }))
  }

  const handleCommentChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setNewReview(prev => ({ ...prev, comment: event.target.value }))
  }

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault()
    if (newReview.rating === 0 || newReview.comment.trim() === '') {
      alert('Please provide both a rating and a comment.')
      return
    }
    const review: Review = {
      id: reviews.length + 1,
      author: 'Anonymous', // In a real app, this would be the logged-in user's name
      rating: newReview.rating,
      comment: newReview.comment,
      date: new Date().toISOString().split('T')[0]
    }
    setReviews(prev => [review, ...prev])
    setNewReview({ rating: 0, comment: '' })
  }

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Customer Reviews</h2>

      {/* Review submission form */}
      <form onSubmit={handleSubmit} className="mb-8 p-4 bg-secondary rounded-lg">
        <h3 className="text-lg font-semibold mb-2">Write a Review</h3>
        <div className="flex mb-2">
          {[1, 2, 3, 4, 5].map((star) => (
            <Star
              key={star}
              className={`w-6 h-6 cursor-pointer ${
                star <= newReview.rating ? 'fill-primary text-primary' : 'text-muted-foreground'
              }`}
              onClick={() => handleStarClick(star)}
            />
          ))}
        </div>
        <Textarea
          placeholder="Write your review here..."
          value={newReview.comment}
          onChange={handleCommentChange}
          className="mb-2"
        />
        <Button type="submit">Submit Review</Button>
      </form>

      {/* Existing reviews */}
      <div className="space-y-4">
        {reviews.map((review) => (
          <div key={review.id} className="border-b border-border pb-4">
            <div className="flex items-center mb-2">
              <Avatar className="w-10 h-10 mr-2">
                <AvatarImage src={`https://api.dicebear.com/6.x/initials/svg?seed=${review.author}`} alt={review.author} />
                <AvatarFallback>{review.author.slice(0, 2)}</AvatarFallback>
              </Avatar>
              <div>
                <p className="font-semibold">{review.author}</p>
                <p className="text-sm text-muted-foreground">{review.date}</p>
              </div>
              <div className="flex ml-auto">
                {[1, 2, 3, 4, 5].map((star) => (
                  <Star
                    key={star}
                    className={`w-4 h-4 ${
                      star <= review.rating ? 'fill-primary text-primary' : 'text-muted-foreground'
                    }`}
                  />
                ))}
              </div>
            </div>
            <p className="text-sm">{review.comment}</p>
          </div>
        ))}
      </div>
    </div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Progress } from "@/components/ui/progress"

export default function Component() {
  const [votes, setVotes] = useState({
    "Strength Training": 0,
    "Cardio": 0,
    "Yoga": 0,
    "HIIT": 0
  })
  const [userVoted, setUserVoted] = useState(false)
  const [totalVotes, setTotalVotes] = useState(0)

  const handleVote = (option: string) => {
    if (!userVoted) {
      setVotes(prev => ({ ...prev, [option]: prev[option] + 1 }))
      setUserVoted(true)
      setTotalVotes(prev => prev + 1)
    }
  }

  useEffect(() => {
    // Simulate live updates
    const interval = setInterval(() => {
      const randomOption = Object.keys(votes)[Math.floor(Math.random() * 4)]
      setVotes(prev => ({ ...prev, [randomOption]: prev[randomOption] + 1 }))
      setTotalVotes(prev => prev + 1)
    }, 3000)

    return () => clearInterval(interval)
  }, [])

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle className="text-2xl font-bold text-center">FitFocus Quick Poll</CardTitle>
      </CardHeader>
      <CardContent>
        <p className="text-center mb-4">What's your favorite type of workout?</p>
        <div className="space-y-4">
          {Object.entries(votes).map(([option, voteCount]) => (
            <div key={option} className="space-y-2">
              <div className="flex justify-between items-center">
                <Button
                  variant={userVoted ? "ghost" : "outline"}
                  className="w-full justify-start"
                  onClick={() => handleVote(option)}
                  disabled={userVoted}
                >
                  {option}
                </Button>
                <span className="text-sm font-medium ml-2">
                  {((voteCount / totalVotes) * 100).toFixed(1)}%
                </span>
              </div>
              <Progress value={(voteCount / totalVotes) * 100} className="h-2" />
            </div>
          ))}
        </div>
        <p className="text-center mt-4 text-sm text-muted-foreground">
          Total votes: {totalVotes}
        </p>
      </CardContent>
    </Card>
  )
}

"use client"

import { useState } from "react"
import { Star } from "lucide-react"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card"

// This would typically come from a database or API
const initialRating = {
  average: 4.2,
  count: 156
}

export default function Component() {
  const [rating, setRating] = useState(0)
  const [hover, setHover] = useState(0)
  const [averageRating, setAverageRating] = useState(initialRating.average)
  const [ratingCount, setRatingCount] = useState(initialRating.count)

  const handleRating = (rate: number) => {
    setRating(rate)
  }

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault()
    if (rating > 0) {
      // In a real application, you would send this rating to your backend
      console.log(`Submitted rating: ${rating}`)

      // Update average rating and count
      const newCount = ratingCount + 1
      const newAverage = (averageRating * ratingCount + rating) / newCount
      setAverageRating(Number(newAverage.toFixed(1)))
      setRatingCount(newCount)

      // Reset user's rating
      setRating(0)
    }
  }

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle className="text-2xl font-bold text-center">TastyBites Recipe Rating</CardTitle>
        <CardDescription className="text-center">Rate this delicious recipe!</CardDescription>
      </CardHeader>
      <CardContent>
        <div className="flex flex-col items-center space-y-4">
          <div className="flex items-center space-x-2">
            {[1, 2, 3, 4, 5].map((star) => (
              <Star
                key={star}
                className={`w-8 h-8 cursor-pointer transition-colors ${
                  (hover || rating) >= star ? "fill-yellow-400 text-yellow-400" : "text-gray-300"
                }`}
                onClick={() => handleRating(star)}
                onMouseEnter={() => setHover(star)}
                onMouseLeave={() => setHover(0)}
              />
            ))}
          </div>
          <div className="text-lg font-semibold">
            Average Rating: {averageRating} ({ratingCount} ratings)
          </div>
        </div>
      </CardContent>
      <CardFooter>
        <form onSubmit={handleSubmit} className="w-full">
          <Button type="submit" className="w-full" disabled={rating === 0}>
            Submit Rating
          </Button>
        </form>
      </CardFooter>
    </Card>
  )
}

