'use client'

import { useState, useEffect } from 'react'
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Button } from "@/components/ui/button"
import { ChevronLeft, ChevronRight } from "lucide-react"

// Testimonial data
const testimonials = [
  {
    id: 1,
    name: "Sarah Johnson",
    photo: "/placeholder.svg?height=100&width=100",
    text: "Emily's photography captured the essence of our wedding day beautifully. Her attention to detail is impeccable!",
  },
  {
    id: 2,
    name: "Michael Chen",
    photo: "/placeholder.svg?height=100&width=100",
    text: "I was blown away by the quality of Emily's corporate headshots. She made everyone feel comfortable and look their best.",
  },
  {
    id: 3,
    name: "Olivia Rodriguez",
    photo: "/placeholder.svg?height=100&width=100",
    text: "Emily has a gift for capturing genuine moments. Our family photos are treasures we'll cherish forever.",
  },
]

export default function TestimonialSlider() {
  const [currentIndex, setCurrentIndex] = useState(0)

  const nextTestimonial = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % testimonials.length)
  }

  const prevTestimonial = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + testimonials.length) % testimonials.length)
  }

  useEffect(() => {
    const timer = setInterval(nextTestimonial, 5000) // Auto-rotate every 5 seconds
    return () => clearInterval(timer)
  }, [])

  const currentTestimonial = testimonials[currentIndex]

  return (
    <div className="w-full max-w-4xl mx-auto p-6 bg-background rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold text-center mb-6">Client Testimonials</h2>
      <div className="relative">
        <div className="flex flex-col md:flex-row items-center gap-6 p-4">
          <Avatar className="w-24 h-24">
            <AvatarImage src={currentTestimonial.photo} alt={currentTestimonial.name} />
            <AvatarFallback>{currentTestimonial.name.split(' ').map(n => n[0]).join('')}</AvatarFallback>
          </Avatar>
          <div className="flex-1 text-center md:text-left">
            <p className="text-lg italic mb-4">&ldquo;{currentTestimonial.text}&rdquo;</p>
            <p className="font-semibold">{currentTestimonial.name}</p>
          </div>
        </div>
        <div className="absolute top-1/2 transform -translate-y-1/2 left-0 right-0 flex justify-between">
          <Button
            variant="ghost"
            size="icon"
            className="rounded-full"
            onClick={prevTestimonial}
            aria-label="Previous testimonial"
          >
            <ChevronLeft className="h-6 w-6" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            className="rounded-full"
            onClick={nextTestimonial}
            aria-label="Next testimonial"
          >
            <ChevronRight className="h-6 w-6" />
          </Button>
        </div>
      </div>
      <div className="flex justify-center mt-4 gap-2">
        {testimonials.map((_, index) => (
          <div
            key={index}
            className={`h-2 w-2 rounded-full ${
              index === currentIndex ? 'bg-primary' : 'bg-gray-300'
            }`}
          />
        ))}
      </div>
    </div>
  )
}

'use client'

import { Progress } from "@/components/ui/progress"
import { Dumbbell } from "lucide-react"
import { useEffect, useState } from "react"

interface ProgressCircleProps {
  progress: number
  size?: 'sm' | 'md' | 'lg'
}

export default function Component({ progress: initialProgress = 0, size = 'md' }: ProgressCircleProps) {
  const [progress, setProgress] = useState(0)

  useEffect(() => {
    const timer = setTimeout(() => setProgress(initialProgress), 100)
    return () => clearTimeout(timer)
  }, [initialProgress])

  const sizeClasses = {
    sm: 'w-24 h-24 text-lg',
    md: 'w-32 h-32 text-xl',
    lg: 'w-40 h-40 text-2xl'
  }

  return (
    <div className={`relative ${sizeClasses[size]} bg-secondary rounded-full`}>
      <Progress
        value={progress}
        className="absolute inset-0 [&>div]:bg-primary [&>div]:rounded-full"
      />
      <div className="absolute inset-0 flex items-center justify-center flex-col">
        <Dumbbell className="w-6 h-6 mb-1 text-primary" />
        <span className="font-bold text-primary">{progress}%</span>
      </div>
    </div>
  )
}

"use client"

import { useState } from "react"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Progress } from "@/components/ui/progress"
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card"
import { AlertCircle, CheckCircle2, File, Upload } from "lucide-react"

interface Document {
  id: string
  name: string
  status: "uploading" | "processing" | "verified" | "rejected"
  progress: number
}

export default function Component() {
  const [documents, setDocuments] = useState<Document[]>([])
  const [error, setError] = useState<string | null>(null)

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0]
    if (!file) return

    setError(null)

    // Simulating file upload and processing
    const newDocument: Document = {
      id: Date.now().toString(),
      name: file.name,
      status: "uploading",
      progress: 0,
    }

    setDocuments((prevDocs) => [...prevDocs, newDocument])

    // Simulate upload progress
    for (let i = 0; i <= 100; i += 10) {
      await new Promise((resolve) => setTimeout(resolve, 200))
      setDocuments((prevDocs) =>
        prevDocs.map((doc) =>
          doc.id === newDocument.id ? { ...doc, progress: i } : doc
        )
      )
    }

    // Simulate processing
    setDocuments((prevDocs) =>
      prevDocs.map((doc) =>
        doc.id === newDocument.id ? { ...doc, status: "processing" } : doc
      )
    )

    await new Promise((resolve) => setTimeout(resolve, 2000))

    // Randomly verify or reject the document
    const finalStatus = Math.random() > 0.5 ? "verified" : "rejected"
    setDocuments((prevDocs) =>
      prevDocs.map((doc) =>
        doc.id === newDocument.id ? { ...doc, status: finalStatus } : doc
      )
    )
  }

  return (
    <Card className="w-full max-w-3xl mx-auto">
      <CardHeader>
        <CardTitle>LegalX Document Upload</CardTitle>
        <CardDescription>Upload your legal documents for verification</CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={(e) => e.preventDefault()} className="space-y-4">
          <div className="grid w-full max-w-sm items-center gap-1.5">
            <Label htmlFor="document">Document</Label>
            <Input
              id="document"
              type="file"
              accept=".pdf,.doc,.docx"
              onChange={handleFileUpload}
            />
          </div>
          {error && (
            <div className="text-red-500 flex items-center gap-2">
              <AlertCircle className="h-4 w-4" />
              {error}
            </div>
          )}
        </form>

        {documents.length > 0 && (
          <div className="mt-8">
            <h3 className="text-lg font-semibold mb-4">Uploaded Documents</h3>
            <ul className="space-y-4">
              {documents.map((doc) => (
                <li key={doc.id} className="flex items-center gap-4">
                  <File className="h-6 w-6 text-blue-500" />
                  <div className="flex-1">
                    <p className="font-medium">{doc.name}</p>
                    <div className="flex items-center gap-2">
                      {doc.status === "uploading" && (
                        <>
                          <Progress value={doc.progress} className="w-full" />
                          <span className="text-sm text-gray-500">{doc.progress}%</span>
                        </>
                      )}
                      {doc.status === "processing" && <p className="text-sm text-yellow-500">Processing...</p>}
                      {doc.status === "verified" && (
                        <p className="text-sm text-green-500 flex items-center gap-1">
                          <CheckCircle2 className="h-4 w-4" /> Verified
                        </p>
                      )}
                      {doc.status === "rejected" && (
                        <p className="text-sm text-red-500 flex items-center gap-1">
                          <AlertCircle className="h-4 w-4" /> Rejected
                        </p>
                      )}
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
      </CardContent>
      <CardFooter>
        <p className="text-sm text-gray-500">
          Supported file types: PDF, DOC, DOCX. Maximum file size: 10MB.
        </p>
      </CardFooter>
    </Card>
  )
}

