"use client"

import { useState, useEffect } from 'react'

export default function Component() {
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  })

  useEffect(() => {
    // Set the date for the token launch (example: 30 days from now)
    const launchDate = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000)

    const timer = setInterval(() => {
      const now = new Date()
      const difference = launchDate.getTime() - now.getTime()

      const days = Math.floor(difference / (1000 * 60 * 60 * 24))
      const hours = Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60))
      const minutes = Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60))
      const seconds = Math.floor((difference % (1000 * 60)) / 1000)

      setTimeLeft({ days, hours, minutes, seconds })

      if (difference < 0) {
        clearInterval(timer)
        setTimeLeft({ days: 0, hours: 0, minutes: 0, seconds: 0 })
      }
    }, 1000)

    return () => clearInterval(timer)
  }, [])

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-primary to-primary-foreground">
      <div className="text-center p-8 rounded-lg shadow-2xl bg-background/80 backdrop-blur-sm">
        <h1 className="text-4xl font-bold mb-4 text-primary">Token Launch Countdown</h1>
        <p className="text-xl mb-8 text-muted-foreground">Get ready for our exciting new token!</p>
        <div className="flex flex-wrap justify-center gap-4">
          {Object.entries(timeLeft).map(([unit, value]) => (
            <div key={unit} className="flex flex-col items-center p-4 bg-primary/10 rounded-lg min-w-[120px]">
              <span className="text-5xl font-bold text-primary">{value}</span>
              <span className="text-sm uppercase text-muted-foreground">{unit}</span>
            </div>
          ))}
        </div>
        <p className="mt-8 text-sm text-muted-foreground">Don't miss out on this opportunity!</p>
      </div>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { ethers } from 'ethers'
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Card, CardContent, CardFooter, CardHeader } from "@/components/ui/card"
import { socialNodeChain } from '@/lib/socialNodeChain'

export default function SocialNodeApp() {
  const [posts, setPosts] = useState([])
  const [newPost, setNewPost] = useState('')
  const [account, setAccount] = useState('')

  useEffect(() => {
    loadBlockchainData()
  }, [])

  async function loadBlockchainData() {
    const provider = new ethers.providers.Web3Provider(window.ethereum)
    const signer = provider.getSigner()
    const address = await signer.getAddress()
    setAccount(address)

    // Load posts from the blockchain
    const fetchedPosts = await socialNodeChain.getPosts()
    setPosts(fetchedPosts)
  }

  async function connectWallet() {
    if (typeof window.ethereum !== 'undefined') {
      try {
        await window.ethereum.request({ method: 'eth_requestAccounts' })
        loadBlockchainData()
      } catch (error) {
        console.error("User denied account access")
      }
    } else {
      console.log('Please install MetaMask!')
    }
  }

  async function createPost(event) {
    event.preventDefault()
    if (newPost.trim() !== '') {
      await socialNodeChain.createPost(newPost)
      setNewPost('')
      // Reload posts after creating a new one
      const updatedPosts = await socialNodeChain.getPosts()
      setPosts(updatedPosts)
    }
  }

  return (
    <div className="container mx-auto p-4">
      <header className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">SocialNode</h1>
        {account ? (
          <div className="flex items-center space-x-2">
            <Avatar>
              <AvatarImage src={`https://avatars.dicebear.com/api/identicon/${account}.svg`} />
              <AvatarFallback>{account.slice(0, 2)}</AvatarFallback>
            </Avatar>
            <span className="text-sm text-gray-600">{`${account.slice(0, 6)}...${account.slice(-4)}`}</span>
          </div>
        ) : (
          <Button onClick={connectWallet}>Connect Wallet</Button>
        )}
      </header>

      <form onSubmit={createPost} className="mb-6">
        <div className="flex space-x-2">
          <Input
            type="text"
            placeholder="What's on your mind?"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
            className="flex-grow"
          />
          <Button type="submit">Post</Button>
        </div>
      </form>

      <div className="space-y-4">
        {posts.map((post, index) => (
          <Card key={index}>
            <CardHeader className="flex items-center space-x-2">
              <Avatar>
                <AvatarImage src={`https://avatars.dicebear.com/api/identicon/${post.author}.svg`} />
                <AvatarFallback>{post.author.slice(0, 2)}</AvatarFallback>
              </Avatar>
              <div>
                <p className="font-semibold">{`${post.author.slice(0, 6)}...${post.author.slice(-4)}`}</p>
                <p className="text-sm text-gray-500">{new Date(post.timestamp * 1000).toLocaleString()}</p>
              </div>
            </CardHeader>
            <CardContent>
              <p>{post.content}</p>
            </CardContent>
            <CardFooter>
              <Button variant="ghost" size="sm">Like</Button>
              <Button variant="ghost" size="sm">Comment</Button>
              <Button variant="ghost" size="sm">Share</Button>
            </CardFooter>
          </Card>
        ))}
      </div>
    </div>
  )
}

import { Badge } from "@/components/ui/badge"
import { Star, Film } from "lucide-react"

interface MovieRatingBadgeProps {
  rating: number
}

export default function Component({ rating = 3.5 }: MovieRatingBadgeProps) {
  const fullStars = Math.floor(rating)
  const hasHalfStar = rating % 1 >= 0.5

  return (
    <Badge variant="secondary" className="flex items-center space-x-2 px-3 py-1">
      <Film className="h-5 w-5 text-primary" />
      <span className="font-bold text-primary">FilmFanatic</span>
      <div className="flex items-center space-x-1">
        {[...Array(5)].map((_, i) => (
          <Star
            key={i}
            className={`h-4 w-4 ${
              i < fullStars
                ? "fill-yellow-400 text-yellow-400"
                : i === fullStars && hasHalfStar
                ? "fill-yellow-400 text-yellow-400 half-star"
                : "text-gray-300"
            }`}
          />
        ))}
      </div>
      <span className="font-semibold">{rating.toFixed(1)}</span>
    </Badge>
  )
}

