theme:
/* Define Color Scheme */
:root {
    --primary-color: #2B6CB0;
    --secondary-color: #4A5568;
    --accent-color: #E53E3E;
    --success-color: #38A169;
    --error-color: #D53F8C;
    --warning-color: #D69E2E;
}

/* Define Typography */
.font-sans {
    font-family: 'Roboto', sans-serif;
}

.font-serif {
    font-family: 'Merriweather', serif;
}

.text-base {
    font-size: 1rem;
    line-height: 1.5;
}

.text-lg {
    font-size: 1.125rem;
    line-height: 1.75;
}

.text-xl {
    font-size: 1.25rem;
    line-height: 1.75;
}

.font-bold {
    font-weight: 700;
}

/* Define Responsive Design */
@media (min-width: 640px) {
    .container {
        max-width: 640px;
    }
}

@media (min-width: 768px) {
    .container {
        max-width: 768px;
    }
}

@media (min-width: 1024px) {
    .container {
        max-width: 1024px;
    }
}

@media (min-width: 1280px) {
    .container {
        max-width: 1280px;
    }
}

